import { Button } from "primereact/button";
import { Dialog } from "primereact/dialog";
import { useState } from "react";
import { useRef } from "react";
import { triggerBase64Download } from "react-base64-downloader";

import { Add } from "./Actions/Add";
import { Menu } from "primereact/menu";
import { data } from "autoprefixer";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { GetFile } from "../../../../../services/ToDoService";
export const File = ({ list }) => {
  const [fileDialog, setFileDialog] = useState(false);

  const actionsTemplate = (row) => {
    var fileId = row.fileId;

    return (
      <>
        <Button
          icon="pi pi-download "
          className="p-button-rounded p-button-outlined"
          onClick={() => {
            GetFile({ fileId }).then((res) => {
              tr;
              console.log(row);
              console.log(atob(res.data.fileString));
              var blob = new Blob([res.data.fileString], { type: row.type });
              const link = document.createElement("a");
              link.href = window.URL.createObjectURL(blob);
              link.download = `${row.name}`;
              link.click();
            });
          }}
        />
        <Button
          icon="pi pi-trash "
          style={{ marginLeft: "1rem" }}
          className="p-button-danger p-button-rounded p-button-outlined"
        />
      </>
    );
  };

  return (
    <>
      <Dialog
        visible={fileDialog}
        draggable={false}
        style={{ width: "80%" }}
        onHide={() => setFileDialog(false)}
      >
        <DataTable
          value={list.files}
          responsiveLayout="scroll"
          rowHover
          emptyMessage="No files"
          footer={
            <div style={{ display: "flex", justifyContent: "right" }}>
              <Add list={list} />
            </div>
          }
        >
          <Column field="name" header="File" />
          <Column
            field="fileId"
            header="Actions"
            body={actionsTemplate}
            style={{ width: "132px" }}
          />
        </DataTable>
      </Dialog>
      <Button
        className="p-button-rounded p-button-info"
        label="File"
        icon="pi pi-file"
        onClick={() => setFileDialog(true)}
      />
    </>
  );
};
