import { useState } from "react";

import { ConfirmDialog } from "primereact/confirmdialog";
import { Button } from "primereact/button";

import { DeleteList } from "../../../../../services/ToDoService";
import { useToastContext } from "../../../../../context/ToastContext";

export default function Delete({ list, loadData }) {
  const toastRef = useToastContext();
  const [deleteListDialog, setDeleteListDialog] = useState(false);
  return (
    <>
      <ConfirmDialog
        visible={deleteListDialog}
        draggable={false}
        onHide={() => setDeleteListDialog(false)}
        icon="pi pi-trash"
        contentStyle={{
          display: "flex",
          justifyContent: "center",
          color: "red",
          textAlign: "center",
        }}
        header={"Delete list?"}
        message={
          <div
            style={{
              display: "flex",

              alignItems: "center",
            }}
          >
            {list.name}
          </div>
        }
        accept={() => {
          DeleteList(list)
            .then((res) => {
              loadData();

              toastRef.current.show({
                severity: "error",
                summary: "Deleted",
                detail: res.message,
                life: 5000,
              });
            })
            .catch((error) => {
              toastRef.current.show({
                severity: "error",
                summary: "Error",
                detail: "Something went wrong",
                life: 5000,
              });
            });
        }}
      />
      <Button
        className="p-button-rounded p-button-danger"
        icon="pi pi-trash"
        label="Delete"
        onClick={() => setDeleteListDialog(true)}
      />
    </>
  );
}
