import React, { useState, useEffect, useReducer } from "react";
import {
  GetListById,
  GetLists,
  GetListsNames,
} from "../../../services/ToDoService";
import Table from "./Table/Table";
import Bar from "./Bar/Bar";
import { useToastContext } from "../../../context/ToastContext";
import Spinner from "../../../UI/Spinner";
import useLocalStorage from "../../../hooks/useLocalStorage";

function loadData() {
  GetListsNames().then((res) => {
    setListNames((prev) => res.data);
    if (res.data.find((x) => x.name === listStorage)) {
      setSelectedListDropdown(res.data.find((x) => x.name === listStorage));
      GetListById(res.data.find((x) => x.name === listStorage).id).then((res) =>
        setList((prev) => res.data)
      );
    } else if (res.data.length > 0) {
      setSelectedListDropdown(res.data[0]);
      GetListById(res.data[0].id).then((res) => setList((prev) => res.data));
    } else {
      setSelectedListDropdown();
      setList();
    }
  });
}

const List = (props) => {
  const toastRef = useToastContext();
  const [list, setList] = useState();
  return (
    <div className="ToDoList">
      <Bar list={list} setList={setList} />
      {list && <Table list={list} />}
    </div>
  );
};

export default List;
