import { Dropdown } from "primereact/dropdown";
import { useEffect } from "react";
import { useState } from "react";
import New from "./Actions/New";
import Delete from "./Actions/Delete";
import Edit from "./Actions/Edit";

import { File } from "./File/File";

import useLocalStorage from "../../../../hooks/useLocalStorage";
import EmptyArr from "../../../../UI/EmptyArr";
import { GetListById, GetListsNames } from "../../../../services/ToDoService";
import Spinner from "../../../../UI/Spinner";

function loadData() {
  GetListsNames().then((res) => {
    setListNames((prev) => res.data);
    if (res.data.find((x) => x.name === listStorage)) {
      setSelectedListDropdown(res.data.find((x) => x.name === listStorage));
      GetListById(res.data.find((x) => x.name === listStorage).id).then((res) =>
        setList((prev) => res.data)
      );
    } else if (res.data.length > 0) {
      setSelectedListDropdown(res.data[0]);
      GetListById(res.data[0].id).then((res) => setList((prev) => res.data));
    } else {
      setSelectedListDropdown();
      setList();
    }
  });
}
function Bar({ list, setList }) {
  const [selectedListDropdown, setSelectedListDropdown] = useState();
  const [listStorage, setListStorage] = useLocalStorage("selectedList");
  const [listNames, setListNames] = useState();
  var dateNow = new Date();
  dateNow.setDate(dateNow.getDate());
  dateNow.setMonth(dateNow.getMonth());
  dateNow.setFullYear(dateNow.getFullYear());

  useEffect(() => {
    loadData();
  }, []);

  return (
    <div className="ListBar" style={{ marginBottom: "1rem" }}>
      {listNames ? (
        listNames.length === 0 ? (
          <EmptyArr addButton={<New dateNow={dateNow} loadData={loadData} />} />
        ) : (
          <div style={{ justifyContent: "space-between", display: "flex" }}>
            <div> {list && <File list={list} loadData={loadData} />}</div>
            <div>
              <Dropdown
                value={selectedListDropdown}
                style={{ marginRight: "1rem", borderRadius: "2em" }}
                optionLabel="name"
                placeholder={"Select list"}
                options={listNames}
                onChange={(e) => {
                  GetListById(
                    listNames.find((x) => x.id === e.value.id).id
                  ).then((res) => {
                    setList((prev) => res.data);
                  });
                  setListStorage(e.value.name);
                  setSelectedListDropdown(e.value);
                }}
              />
              <New dateNow={dateNow} loadData={loadData} />
              {list && (
                <>
                  <Edit list={list} setList={setList} loadData={loadData} />
                  <Delete list={list} loadData={loadData} />
                </>
              )}
            </div>
          </div>
        )
      ) : (
        <Spinner />
      )}
    </div>
  );
}

export default Bar;
