import { Dropdown } from "primereact/dropdown";
import { useEffect } from "react";

import New from "./Actions/New";
import Delete from "./Actions/Delete";
import Edit from "./Actions/Edit";

import { File } from "./File/File";

import useLocalStorage from "../../../../hooks/useLocalStorage";
import EmptyArr from "../../../../UI/EmptyArr";
import { GetListById } from "../../../../services/ToDoService";

function Bar({ listNames, setListNames, setList, list }) {
  const [listStorage, setListStorage] = useLocalStorage("selectedList");
  var dateNow = new Date();
  dateNow.setDate(dateNow.getDate());
  dateNow.setMonth(dateNow.getMonth());
  dateNow.setFullYear(dateNow.getFullYear());

  useEffect(() => {
    if (listNames.find((x) => x.name === listStorage)) {
      GetListById(listNames.find((x) => x.name === listStorage).id).then(
        (res) => setList((prev) => res.data)
      );
    }
  }, []);
  return (
    <div className="ListBar" style={{ marginBottom: "1rem" }}>
      {listNames.length === 0 ? (
        <EmptyArr addButton={<New dateNow={dateNow} />} />
      ) : (
        <div style={{ justifyContent: "space-between", display: "flex" }}>
          <div> {list && <File list={list} />} </div>
          <div>
            <Dropdown
              value={listNames}
              style={{ marginRight: "1rem", borderRadius: "2em" }}
              optionLabel="name"
              placeholder={"Select list"}
              options={listNames}
              onChange={(e) => {
                GetListById(listNames.find((x) => x.name === e.value).id).then(
                  (res) => setList((prev) => res.data)
                );
                setListStorage(e.value.name);
              }}
            />
            <New dateNow={dateNow} />
            {false && (
              <>
                <Edit list={list} setList={setList} />
                <Delete list={list} setList={setList} />
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Bar;
